glsl4.vert:
precision highp float;
layout(binding = 23) uniform CCGlobal {
  highp   vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
  mediump vec4 cc_nativeSize;
  highp   mat4 cc_matView;
  highp   mat4 cc_matViewInv;
  highp   mat4 cc_matProj;
  highp   mat4 cc_matProjInv;
  highp   mat4 cc_matViewProj;
  highp   mat4 cc_matViewProjInv;
  highp   vec4 cc_cameraPos;
  mediump vec4 cc_exposure;
  mediump vec4 cc_mainLitDir;
  mediump vec4 cc_mainLitColor;
  mediump vec4 cc_ambientSky;
  mediump vec4 cc_ambientGround;
};
layout(location = 0) in vec3 a_position;
layout(location = 0) out vec4 viewDir;
vec4 vert () {
  viewDir = vec4(a_position, 1.0);
  mat4 matViewRotOnly = mat4(mat3(cc_matView));
  vec4 pos = matViewRotOnly * viewDir;
  vec2 f = cc_matProj[3][3] > 0.0 ? vec2(4.8, 2.4) : vec2(cc_matProj[1][1]);
  pos.xy *= vec2(cc_screenSize.y * cc_screenSize.z, 1.0) * f;
  pos.zw = vec2(-0.99999 * pos.z, -pos.z);
  return pos;
}
void main() { gl_Position = vert(); }

glsl4.frag:
precision mediump float;
layout(binding = 23) uniform CCGlobal {
  highp   vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
  mediump vec4 cc_nativeSize;
  highp   mat4 cc_matView;
  highp   mat4 cc_matViewInv;
  highp   mat4 cc_matProj;
  highp   mat4 cc_matProjInv;
  highp   mat4 cc_matViewProj;
  highp   mat4 cc_matViewProjInv;
  highp   vec4 cc_cameraPos;
  mediump vec4 cc_exposure;
  mediump vec4 cc_mainLitDir;
  mediump vec4 cc_mainLitColor;
  mediump vec4 cc_ambientSky;
  mediump vec4 cc_ambientGround;
};
layout(binding = 26) uniform samplerCube cc_environment;
vec3 unpackRGBE (vec4 rgbe) {
  return rgbe.rgb * pow(2.0, rgbe.a * 255.0 - 128.0);
}
vec3 SRGBToLinear (vec3 gamma) {
  return gamma * gamma;
}
vec3 ACESToneMap (vec3 color) {
  color = min(color, vec3(8.0));
  const float A = 2.51;
  const float B = 0.03;
  const float C = 2.43;
  const float D = 0.59;
  const float E = 0.14;
  return (color * (A * color + B)) / (color * (C * color + D) + E);
}
vec4 CCFragOutput (vec4 color) {
  #if !CC_USE_HDR
    color.rgb = sqrt(ACESToneMap(color.rgb));
  #endif
  return color;
}
layout(location = 0) in vec4 viewDir;
vec4 frag () {
  #if USE_RGBE_CUBEMAP
    vec3 c = unpackRGBE(texture(cc_environment, viewDir.xyz));
  #else
    vec3 c = SRGBToLinear(texture(cc_environment, viewDir.xyz).rgb) * cc_ambientSky.w;
  #endif
  return CCFragOutput(vec4(c, 1.0));
}
layout(location = 0) out vec4 cc_FragColor;
void main() { cc_FragColor = frag(); }

