glsl4.vert:
precision mediump float;
layout(binding = 23) uniform CCGlobal {
  highp   vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
  mediump vec4 cc_nativeSize;
  highp   mat4 cc_matView;
  highp   mat4 cc_matViewInv;
  highp   mat4 cc_matProj;
  highp   mat4 cc_matProjInv;
  highp   mat4 cc_matViewProj;
  highp   mat4 cc_matViewProjInv;
  highp   vec4 cc_cameraPos;
  mediump vec4 cc_exposure;
  mediump vec4 cc_mainLitDir;
  mediump vec4 cc_mainLitColor;
  mediump vec4 cc_ambientSky;
  mediump vec4 cc_ambientGround;
};
layout(binding = 21) uniform CCLocal {
  highp mat4 cc_matWorld;
  highp mat4 cc_matWorldIT;
};
layout(location = 0) in vec3 a_position;
layout(location = 0) out vec4 wposition;
vec4 vert () {
  vec4 pos = vec4(a_position, 1);
  wposition = cc_matWorld * pos;
  wposition.y += 0.01;
  pos = cc_matViewProj * wposition;
  return pos;
}
void main() { gl_Position = vert(); }

glsl4.frag:
precision mediump float;
layout(binding = 23) uniform CCGlobal {
  highp   vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
  mediump vec4 cc_nativeSize;
  highp   mat4 cc_matView;
  highp   mat4 cc_matViewInv;
  highp   mat4 cc_matProj;
  highp   mat4 cc_matProjInv;
  highp   mat4 cc_matViewProj;
  highp   mat4 cc_matViewProjInv;
  highp   vec4 cc_cameraPos;
  mediump vec4 cc_exposure;
  mediump vec4 cc_mainLitDir;
  mediump vec4 cc_mainLitColor;
  mediump vec4 cc_ambientSky;
  mediump vec4 cc_ambientGround;
};
vec3 SRGBToLinear (vec3 gamma) {
  return gamma * gamma;
}
vec4 CCFragOutput (vec4 color) {
  #if CC_USE_HDR
    color.rgb = mix(color.rgb, SRGBToLinear(color.rgb) * cc_exposure.w, vec3(cc_exposure.z));
	#endif
	return color;
}
layout(location = 0) in vec4 wposition;
layout(binding = 0) uniform TexCoords {
  vec4 BrushPos;
  vec4 BrushParams;
};
vec4 frag () {
  float Radius = BrushParams.x;
  float Falloff = BrushParams.y;
  float Distance = length(wposition.xz - BrushPos.xz);
  float k = 0.0;
  #if LINEAR
    if (Distance <= Radius) {
      k = 1.0;
    }
    else if (Distance > Radius + Falloff) {
      k = 0.0;
    }
    else {
      k = max(0.0, 1.0 - (Distance - Radius) / Falloff);
    }
  #elif SMOOTH
    if (Distance <= Radius) {
      k = 1.0;
    }
    else if (Distance > Radius + Falloff) {
      k = 0.0;
    }
    else {
      float y = (Distance - Radius) / Falloff;
      k = sqrt(1.0 - y * y);
    }
  #elif SPHERICAL
    if (Distance <= Radius) {
      k = 1.0;
    }
    else if (Distance > Radius + Falloff) {
      k = 0.0;
    }
    else {
      k = max(0.0, 1.0 - (Distance - Radius) / Falloff);
    }
    k = k*k*(3 - 2 * k);
  #elif TIP
    if (Distance <= Radius) {
      k = 1.0;
    }
    else if (Distance > Radius + Falloff) {
      k = 0.0;
    }
    else {
      float y = (Falloff + Radius - Distance) / Falloff;
      k = 1.0 - sqrt(1.0 - y * y);
    }
  #endif
  vec4 color = vec4(0, 0, 0, 0);
  color.rgb = vec3(100, 100, 135) / 255.0;
  color.a = 0.85 * k;
  return CCFragOutput(color);
}
layout(location = 0) out vec4 cc_FragColor;
void main() { cc_FragColor = frag(); }

